/**
 * PathogenExplorer Component
 * Provides comprehensive pathogen exploration functionality
 * Allows users to search, filter, and explore pathogens and their associated conditions
 */

import React from 'react';
import { Search, Filter, Microscope, Target, BookOpen, ArrowRight, TrendingUp } from 'lucide-react';

const PathogenExplorer = ({ 
  pathogenData, 
  onSelectCondition 
}) => {
  const {
    pathogens,
    selectedPathogen,
    selectedPathogenConditions,
    selectedPathogenAntibiotics,
    pathogenStats,
    filteredStats,
    searchQuery,
    gramFilter,
    typeFilter,
    sortBy,
    searchPathogens,
    filterByGramStatus,
    filterByType,
    setSortOrder,
    selectPathogen,
    clearSelection,
    clearFilters,
    findSimilarPathogens,
    isLoading
  } = pathogenData;

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="text-gray-500">Loading pathogen data...</div>
      </div>
    );
  }

  const getGramStatusColor = (gramStatus) => {
    switch (gramStatus) {
      case 'positive': return 'text-purple-600 bg-purple-100';
      case 'negative': return 'text-pink-600 bg-pink-100';
      default: return 'text-gray-600 bg-gray-100';
    }
  };

  const getGramStatusLabel = (gramStatus) => {
    switch (gramStatus) {
      case 'positive': return 'Gram(+)';
      case 'negative': return 'Gram(-)';
      default: return 'Unknown';
    }
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="bg-white rounded-xl shadow-sm border p-6">
        <div className="flex items-center gap-3 mb-4">
          <Microscope className="text-blue-600" size={24} />
          <h1 className="text-2xl font-bold text-gray-900">Pathogen Explorer</h1>
        </div>
        <p className="text-gray-600">
          Explore infectious pathogens and discover associated conditions and treatment options.
        </p>

        {/* Statistics */}
        {pathogenStats && (
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mt-6">
            <div className="text-center p-3 bg-blue-50 rounded-lg">
              <div className="text-xl font-bold text-blue-600">{pathogenStats.total}</div>
              <div className="text-sm text-gray-600">Total Pathogens</div>
            </div>
            <div className="text-center p-3 bg-purple-50 rounded-lg">
              <div className="text-xl font-bold text-purple-600">{pathogenStats.gramPositive}</div>
              <div className="text-sm text-gray-600">Gram Positive</div>
            </div>
            <div className="text-center p-3 bg-pink-50 rounded-lg">
              <div className="text-xl font-bold text-pink-600">{pathogenStats.gramNegative}</div>
              <div className="text-sm text-gray-600">Gram Negative</div>
            </div>
            <div className="text-center p-3 bg-green-50 rounded-lg">
              <div className="text-xl font-bold text-green-600">{pathogenStats.avgConditions}</div>
              <div className="text-sm text-gray-600">Avg Conditions</div>
            </div>
          </div>
        )}
      </div>

      <div className="grid lg:grid-cols-2 gap-6">
        {/* Search and Filter Panel */}
        <div className="bg-white rounded-xl shadow-sm border p-6">
          <div className="flex items-center gap-2 mb-4">
            <Search size={20} className="text-gray-600" />
            <h2 className="text-lg font-semibold">Search & Filter</h2>
          </div>

          {/* Search Input */}
          <div className="mb-4">
            <input
              type="text"
              placeholder="Search pathogens..."
              value={searchQuery}
              onChange={(e) => searchPathogens(e.target.value)}
              className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>

          {/* Filters */}
          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Gram Status
              </label>
              <select
                value={gramFilter}
                onChange={(e) => filterByGramStatus(e.target.value)}
                className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              >
                <option value="all">All</option>
                <option value="positive">Gram Positive</option>
                <option value="negative">Gram Negative</option>
                <option value="unknown">Unknown</option>
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Pathogen Type
              </label>
              <select
                value={typeFilter}
                onChange={(e) => filterByType(e.target.value)}
                className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              >
                <option value="all">All Types</option>
                <option value="bacteria">Bacteria</option>
                <option value="virus">Virus</option>
                <option value="fungus">Fungus</option>
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Sort By
              </label>
              <select
                value={sortBy}
                onChange={(e) => setSortOrder(e.target.value)}
                className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              >
                <option value="name">Name (A-Z)</option>
                <option value="count">Usage Count</option>
                <option value="conditions">Condition Count</option>
              </select>
            </div>

            <button
              onClick={clearFilters}
              className="w-full px-4 py-2 text-gray-600 border border-gray-300 rounded-lg hover:bg-gray-50 transition-colors"
            >
              Clear Filters
            </button>
          </div>

          {/* Filtered Statistics */}
          {filteredStats && (
            <div className="mt-6 p-4 bg-gray-50 rounded-lg">
              <h3 className="font-medium text-gray-900 mb-2">Filtered Results</h3>
              <div className="text-sm text-gray-600 space-y-1">
                <div>Total: {filteredStats.total} pathogens</div>
                <div>Gram(+): {filteredStats.gramPositive}</div>
                <div>Gram(-): {filteredStats.gramNegative}</div>
                <div>Unknown: {filteredStats.unknown}</div>
              </div>
            </div>
          )}
        </div>

        {/* Pathogen List */}
        <div className="bg-white rounded-xl shadow-sm border p-6">
          <div className="flex items-center justify-between mb-4">
            <h2 className="text-lg font-semibold">Pathogens</h2>
            <span className="text-sm text-gray-600">{pathogens.length} found</span>
          </div>

          <div className="space-y-2 max-h-96 overflow-y-auto">
            {pathogens.map((pathogen, index) => (
              <div
                key={index}
                onClick={() => selectPathogen(pathogen)}
                className={`p-3 border rounded-lg cursor-pointer transition-colors ${
                  selectedPathogen?.name === pathogen.name
                    ? 'border-blue-500 bg-blue-50'
                    : 'border-gray-200 hover:border-gray-300 hover:bg-gray-50'
                }`}
              >
                <div className="flex items-center justify-between">
                  <div className="flex-1">
                    <div className="font-medium text-gray-900">{pathogen.name}</div>
                    {pathogen.details && (
                      <div className="text-xs text-gray-500 mt-1">{pathogen.details}</div>
                    )}
                  </div>
                  <div className="flex items-center gap-2">
                    <span className={`px-2 py-1 rounded-full text-xs font-medium ${getGramStatusColor(pathogen.gramStatus)}`}>
                      {getGramStatusLabel(pathogen.gramStatus)}
                    </span>
                    <span className="text-sm text-gray-600">{pathogen.conditions.length}</span>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Selected Pathogen Details */}
      {selectedPathogen && (
        <div className="bg-white rounded-xl shadow-sm border p-6">
          <div className="flex items-center justify-between mb-4">
            <div>
              <h2 className="text-xl font-semibold">{selectedPathogen.name}</h2>
              <div className="flex items-center gap-2 mt-1">
                <span className={`px-2 py-1 rounded-full text-xs font-medium ${getGramStatusColor(selectedPathogen.gramStatus)}`}>
                  {getGramStatusLabel(selectedPathogen.gramStatus)}
                </span>
                <span className="text-sm text-gray-600 capitalize">{selectedPathogen.type}</span>
              </div>
            </div>
            <button
              onClick={clearSelection}
              className="text-gray-500 hover:text-gray-700"
            >
              âœ•
            </button>
          </div>

          {selectedPathogen.details && (
            <div className="mb-6 p-4 bg-blue-50 rounded-lg">
              <div className="text-sm text-blue-800">{selectedPathogen.details}</div>
            </div>
          )}

          <div className="grid md:grid-cols-2 gap-6">
            {/* Associated Conditions */}
            <div>
              <h3 className="text-lg font-medium mb-3 flex items-center gap-2">
                <Target size={18} className="text-green-600" />
                Associated Conditions ({selectedPathogenConditions.length})
              </h3>
              <div className="space-y-2">
                {selectedPathogenConditions.map((condition, index) => (
                  <div
                    key={index}
                    onClick={() => onSelectCondition(condition)}
                    className="p-3 border rounded-lg cursor-pointer hover:border-blue-300 hover:bg-blue-50 transition-colors"
                  >
                    <div className="flex items-center justify-between">
                      <div>
                        <div className="font-medium text-gray-900">{condition.name}</div>
                        <div className="text-sm text-gray-600">{condition.category}</div>
                      </div>
                      <ArrowRight size={16} className="text-gray-400" />
                    </div>
                  </div>
                ))}
              </div>
            </div>

            {/* Treatment Options */}
            <div>
              <h3 className="text-lg font-medium mb-3 flex items-center gap-2">
                <BookOpen size={18} className="text-purple-600" />
                Treatment Options ({selectedPathogenAntibiotics.length})
              </h3>
              <div className="space-y-2">
                {selectedPathogenAntibiotics.map((antibiotic, index) => (
                  <div key={index} className="p-3 border rounded-lg">
                    <div className="flex items-center justify-between">
                      <div>
                        <div className="font-medium text-gray-900">{antibiotic.name}</div>
                        <div className="text-sm text-gray-600">{antibiotic.class}</div>
                      </div>
                      <div className="flex items-center gap-2">
                        <TrendingUp size={14} className="text-green-600" />
                        <span className="text-sm text-green-600">{antibiotic.effectivenessScore}</span>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>

          {/* Similar Pathogens */}
          {(() => {
            const similarPathogens = findSimilarPathogens(selectedPathogen);
            return similarPathogens.length > 0 && (
              <div className="mt-6">
                <h3 className="text-lg font-medium mb-3">Similar Pathogens</h3>
                <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-5 gap-2">
                  {similarPathogens.map((pathogen, index) => (
                    <button
                      key={index}
                      onClick={() => selectPathogen(pathogen)}
                      className="p-2 text-left border rounded-lg hover:border-blue-300 hover:bg-blue-50 transition-colors"
                    >
                      <div className="text-sm font-medium text-gray-900">{pathogen.name}</div>
                      <div className={`text-xs ${getGramStatusColor(pathogen.gramStatus)} inline-block px-1 rounded mt-1`}>
                        {getGramStatusLabel(pathogen.gramStatus)}
                      </div>
                    </button>
                  ))}
                </div>
              </div>
            );
          })()}
        </div>
      )}
    </div>
  );
};

export default PathogenExplorer;
