import React, { useState } from 'react';

// Import Error Boundary
import ErrorBoundary from './components/ErrorBoundary';

// Import our components and hooks
import Header from './components/Header';
import HomeTab from './components/HomeTab';
import ConditionsTab from './components/ConditionsTab';
import QuizTab from './components/QuizTab';
import ConditionDetailModal from './components/ConditionDetailModal';
import UserProgress from './components/UserProgress';
import PathogenExplorer from './components/PathogenExplorer';
import AntibioticExplorer from './components/AntibioticExplorer';
import useResponsive from './hooks/useResponsive';
import useSearch from './hooks/useSearch';
import useQuizProgress from './hooks/useQuizProgress';
import useBookmarks from './hooks/useBookmarks';
import usePathogenData from './hooks/usePathogenData';
import useAntibioticData from './hooks/useAntibioticData';

// Import our data
import medicalConditions from './data/medicalConditions';
import quizQuestions from './data/quizQuestions';

function App() {
  console.log('App component starting render...');

  // State management
  const [activeTab, setActiveTab] = useState('home');
  const [selectedCondition, setSelectedCondition] = useState(null);
  const [showMobileMenu, setShowMobileMenu] = useState(false);

  console.log('App state initialized...');

  // Use our custom hooks with error handling
  let isMobile, quizProgress, bookmarks, pathogenData, antibioticData, searchData;

  try {
    isMobile = useResponsive();
    console.log('useResponsive hook success');
  } catch (error) {
    console.error('useResponsive hook failed:', error);
    isMobile = false;
  }

  try {
    quizProgress = useQuizProgress();
    console.log('useQuizProgress hook success');
  } catch (error) {
    console.error('useQuizProgress hook failed:', error);
    quizProgress = { clearHistory: () => {} };
  }

  try {
    bookmarks = useBookmarks();
    console.log('useBookmarks hook success');
  } catch (error) {
    console.error('useBookmarks hook failed:', error);
    bookmarks = { 
      bookmarkedConditions: [],
      isBookmarked: () => false,
      toggleBookmark: () => {}
    };
  }

  try {
    pathogenData = usePathogenData(medicalConditions);
    console.log('usePathogenData hook success');
  } catch (error) {
    console.error('usePathogenData hook failed:', error);
    pathogenData = { 
      pathogens: [],
      isLoading: false
    };
  }

  try {
    antibioticData = useAntibioticData(medicalConditions);
    console.log('useAntibioticData hook success');
  } catch (error) {
    console.error('useAntibioticData hook failed:', error);
    antibioticData = { 
      antibiotics: [],
      isLoading: false
    };
  }

  // Use search hook for conditions
  try {
    const searchFields = ['name', 'category', 'commonPathogens', 'description'];
    searchData = useSearch(medicalConditions, searchFields);
    console.log('useSearch hook success');
  } catch (error) {
    console.error('useSearch hook failed:', error);
    searchData = {
      searchTerm: '',
      setSearchTerm: () => {},
      filteredItems: medicalConditions
    };
  }

  const { searchTerm, setSearchTerm, filteredItems: filteredConditions } = searchData;

  console.log('All hooks completed, starting render...');

  return (
    <ErrorBoundary>
      <div className="min-h-screen bg-slate-50 font-sans">
        {/* Use our new Header component */}
        <ErrorBoundary>
          <Header 
            activeTab={activeTab}
            setActiveTab={setActiveTab}
            isMobile={isMobile}
            showMobileMenu={showMobileMenu}
            setShowMobileMenu={setShowMobileMenu}
          />
        </ErrorBoundary>

        {/* Main Content */}
        <main className="max-w-6xl mx-auto p-4 md:p-8">
          {/* Render appropriate tab component based on activeTab */}
          {activeTab === 'home' && (
            <ErrorBoundary>
              <HomeTab setActiveTab={setActiveTab} />
            </ErrorBoundary>
          )}

          {activeTab === 'conditions' && (
            <ErrorBoundary>
              <ConditionsTab 
                filteredConditions={filteredConditions}
                setSelectedCondition={setSelectedCondition}
                searchTerm={searchTerm}
                setSearchTerm={setSearchTerm}
              />
            </ErrorBoundary>
          )}

          {activeTab === 'quiz' && (
            <ErrorBoundary>
              <QuizTab 
                quizQuestions={quizQuestions}
                setActiveTab={setActiveTab}
                quizProgress={quizProgress}
              />
            </ErrorBoundary>
          )}

          {activeTab === 'progress' && (
            <ErrorBoundary>
              <UserProgress 
                userProgress={quizProgress}
                bookmarkedConditions={bookmarks.bookmarkedConditions}
                onViewCondition={setSelectedCondition}
                onClearProgress={quizProgress.clearHistory}
              />
            </ErrorBoundary>
          )}

          {activeTab === 'pathogens' && (
            <ErrorBoundary>
              <PathogenExplorer 
                pathogenData={pathogenData}
                onSelectCondition={setSelectedCondition}
              />
            </ErrorBoundary>
          )}

          {activeTab === 'antibiotics' && (
            <ErrorBoundary>
              <AntibioticExplorer 
                antibioticData={antibioticData}
                onSelectCondition={setSelectedCondition}
              />
            </ErrorBoundary>
          )}
        </main>

        {/* Condition Detail Modal */}
        <ErrorBoundary>
          <ConditionDetailModal 
            condition={selectedCondition}
            onClose={() => setSelectedCondition(null)}
            isBookmarked={selectedCondition ? bookmarks.isBookmarked(selectedCondition.name) : false}
            onToggleBookmark={() => selectedCondition && bookmarks.toggleBookmark(selectedCondition)}
            allConditions={medicalConditions}
          />
        </ErrorBoundary>
      </div>
    </ErrorBoundary>
  );
}

export default App;
