/**
 * Header Component
 * Handles the main navigation and mobile menu for the application
 * 
 * Props:
 * - activeTab: string - currently active tab ('home', 'conditions', 'quiz')
 * - setActiveTab: function - function to change the active tab
 * - isMobile: boolean - whether the screen is mobile size
 * - showMobileMenu: boolean - whether mobile menu is open
 * - setShowMobileMenu: function - function to toggle mobile menu
 */

import React from 'react';
import { Menu, Home, BookOpen, GraduationCap, Stethoscope, TrendingUp } from 'lucide-react';

const Header = ({ 
  activeTab, 
  setActiveTab, 
  isMobile, 
  showMobileMenu, 
  setShowMobileMenu 
}) => {
  return (
    <header className="bg-blue-700 text-white p-4 shadow-md sticky top-0 z-50">
      <div className="flex justify-between items-center max-w-6xl mx-auto">
        {/* Logo */}
        <div className="flex items-center gap-2 text-xl font-bold">
          <Stethoscope size={24} />
          MedLearn
        </div>

        {/* Desktop Navigation */}
        {!isMobile && (
          <nav className="flex gap-8">
            <div
              className={`flex items-center gap-2 cursor-pointer p-2 rounded-md transition-colors ${
                activeTab === 'home' ? 'bg-white bg-opacity-20' : 'hover:bg-white hover:bg-opacity-10'
              }`}
              onClick={() => setActiveTab('home')}
            >
              <Home size={20} />
              Home
            </div>
            <div
              className={`flex items-center gap-2 cursor-pointer p-2 rounded-md transition-colors ${
                activeTab === 'conditions' ? 'bg-white bg-opacity-20' : 'hover:bg-white hover:bg-opacity-10'
              }`}
              onClick={() => setActiveTab('conditions')}
            >
              <BookOpen size={20} />
              Conditions
            </div>
            <div
              className={`flex items-center gap-2 cursor-pointer p-2 rounded-md transition-colors ${
                activeTab === 'quiz' ? 'bg-white bg-opacity-20' : 'hover:bg-white hover:bg-opacity-10'
              }`}
              onClick={() => setActiveTab('quiz')}
            >
              <GraduationCap size={20} />
              Quiz
            </div>
            <div
              className={`flex items-center gap-2 cursor-pointer p-2 rounded-md transition-colors ${
                activeTab === 'progress' ? 'bg-white bg-opacity-20' : 'hover:bg-white hover:bg-opacity-10'
              }`}
              onClick={() => setActiveTab('progress')}
            >
              <TrendingUp size={20} />
              Progress
            </div>
          </nav>
        )}

        {/* Mobile Menu Button */}
        {isMobile && (
          <button 
            className="p-2"
            onClick={() => setShowMobileMenu(!showMobileMenu)}
            aria-label="Toggle mobile menu"
          >
            <Menu size={24} />
          </button>
        )}
      </div>

      {/* Mobile Menu */}
      {isMobile && showMobileMenu && (
        <div className="border-t border-blue-600 mt-4 pt-4">
          <div
            className={`flex items-center gap-2 cursor-pointer p-2 rounded-md mb-2 ${
              activeTab === 'home' ? 'bg-white bg-opacity-20' : ''
            }`}
            onClick={() => {
              setActiveTab('home');
              setShowMobileMenu(false);
            }}
          >
            <Home size={20} />
            Home
          </div>
          <div
            className={`flex items-center gap-2 cursor-pointer p-2 rounded-md mb-2 ${
              activeTab === 'conditions' ? 'bg-white bg-opacity-20' : ''
            }`}
            onClick={() => {
              setActiveTab('conditions');
              setShowMobileMenu(false);
            }}
          >
            <BookOpen size={20} />
            Conditions
          </div>
          <div
            className={`flex items-center gap-2 cursor-pointer p-2 rounded-md mb-2 ${
              activeTab === 'quiz' ? 'bg-white bg-opacity-20' : ''
            }`}
            onClick={() => {
              setActiveTab('quiz');
              setShowMobileMenu(false);
            }}
          >
            <GraduationCap size={20} />
            Quiz
          </div>
          <div
            className={`flex items-center gap-2 cursor-pointer p-2 rounded-md ${
              activeTab === 'progress' ? 'bg-white bg-opacity-20' : ''
            }`}
            onClick={() => {
              setActiveTab('progress');
              setShowMobileMenu(false);
            }}
          >
            <TrendingUp size={20} />
            Progress
          </div>
        </div>
      )}
    </header>
  );
};

export default Header;
