import React, { useState } from 'react';

// Test data imports
import medicalConditions from './data/medicalConditions';
import quizQuestions from './data/quizQuestions';

// Test buildIndexes function
import { buildIndexes } from './utils/dataIndexer';

// Test basic hooks
import useResponsive from './hooks/useResponsive';
import useSearch from './hooks/useSearch';

// Test problematic hooks
import usePathogenData from './hooks/usePathogenData';
import useAntibioticData from './hooks/useAntibioticData';

// Test components
import HomeTab from './components/HomeTab';

function App() {
  console.log('App component rendering...');
  console.log('Medical conditions loaded:', medicalConditions?.length);
  console.log('Quiz questions loaded:', quizQuestions?.length);

  // Test React state
  const [testState] = useState('React state working');

  // Test buildIndexes
  let indexesResult = 'not tested';
  try {
    const indexes = buildIndexes(medicalConditions);
    indexesResult = `Success - ${indexes.pathogens?.length || 0} pathogens, ${indexes.antibiotics?.length || 0} antibiotics`;
    console.log('Indexes built successfully:', indexes);
  } catch (error) {
    indexesResult = `Error: ${error.message}`;
    console.error('buildIndexes failed:', error);
  }

  // Test basic hooks
  let hooksResult = 'not tested';
  try {
    const isMobile = useResponsive();
    const searchFields = ['name', 'category'];
    const { searchTerm, setSearchTerm, filteredItems } = useSearch(medicalConditions, searchFields);
    hooksResult = `Success - Mobile: ${isMobile}, Search items: ${filteredItems?.length || 0}`;
    console.log('Basic hooks working:', { isMobile, searchItemsCount: filteredItems?.length });
  } catch (error) {
    hooksResult = `Error: ${error.message}`;
    console.error('Basic hooks failed:', error);
  }

  // Test problematic hooks
  let pathogenHooksResult = 'not tested';
  try {
    const pathogenData = usePathogenData(medicalConditions);
    const antibioticData = useAntibioticData(medicalConditions);
    pathogenHooksResult = `Success - Pathogens: ${pathogenData.pathogens?.length || 0}, Antibiotics: ${antibioticData.antibiotics?.length || 0}`;
    console.log('Pathogen hooks working:', { 
      pathogenCount: pathogenData.pathogens?.length, 
      antibioticCount: antibioticData.antibiotics?.length 
    });
  } catch (error) {
    pathogenHooksResult = `Error: ${error.message}`;
    console.error('Pathogen hooks failed:', error);
  }

  return (
    <div className="min-h-screen bg-slate-50 font-sans">
      <div className="p-8">
        <h1 className="text-4xl font-bold text-center py-8">
          Debug Results:
        </h1>
        <div className="space-y-2 max-w-4xl mx-auto">
          <p className="text-center text-gray-600">
            Medical Conditions: {medicalConditions?.length || 0} loaded
          </p>
          <p className="text-center text-gray-600">
            Quiz Questions: {quizQuestions?.length || 0} loaded
          </p>
          <p className="text-center text-gray-600">
            BuildIndexes: {indexesResult}
          </p>
          <p className="text-center text-gray-600">
            Basic Hooks: {hooksResult}
          </p>
          <p className="text-center text-gray-600">
            Pathogen Hooks: {pathogenHooksResult}
          </p>
          <p className="text-center text-gray-600">
            Test State: {testState}
          </p>
        </div>

        <div className="mt-8">
          <h2 className="text-2xl font-bold text-center mb-4">Testing HomeTab Component:</h2>
          <HomeTab setActiveTab={() => console.log('Tab changed')} />
        </div>
      </div>
    </div>
  );
}

export default App;
