import React, { Suspense, lazy } from 'react';

// Import Error Boundary and Context
import ErrorBoundary from './components/ErrorBoundary';
import { AppProvider, useAppContext } from './contexts/AppContext';

// Import our core components
import Header from './components/Header';
import HomeTab from './components/HomeTab';
import ConditionsTab from './components/ConditionsTab';
import QuizTab from './components/QuizTab';
import ConditionDetailModal from './components/ConditionDetailModal';
import UserProgress from './components/UserProgress';
import LoadingSpinner from './components/LoadingSpinner';

// Lazy load heavy components
const PathogenExplorer = lazy(() => import('./components/PathogenExplorer'));
const AntibioticExplorer = lazy(() => import('./components/AntibioticExplorer'));

// Import our data
import quizQuestions from './data/quizQuestions';

/**
 * AppContent Component
 * Contains the main application logic and uses context for state management
 */
const AppContent = () => {
  console.log('App component starting render...');

  // Get all state and data from context
  const {
    activeTab,
    setActiveTab,
    selectedCondition,
    setSelectedCondition,
    showMobileMenu,
    setShowMobileMenu,
    isMobile,
    quizProgress,
    bookmarks,
    pathogenData,
    antibioticData,
    searchData,
    medicalConditions
  } = useAppContext();

  const { searchTerm, setSearchTerm, filteredItems: filteredConditions } = searchData;

  console.log('Context data loaded, starting render...');

  return (
    <ErrorBoundary>
      <div className="min-h-screen bg-slate-50 font-sans">
        {/* Use our new Header component */}
        <ErrorBoundary>
          <Header 
            activeTab={activeTab}
            setActiveTab={setActiveTab}
            isMobile={isMobile}
            showMobileMenu={showMobileMenu}
            setShowMobileMenu={setShowMobileMenu}
          />
        </ErrorBoundary>

        {/* Main Content */}
        <main className="max-w-6xl mx-auto p-4 md:p-8">
          {/* Render appropriate tab component based on activeTab */}
          {activeTab === 'home' && (
            <ErrorBoundary>
              <HomeTab setActiveTab={setActiveTab} />
            </ErrorBoundary>
          )}

          {activeTab === 'conditions' && (
            <ErrorBoundary>
              <ConditionsTab 
                filteredConditions={filteredConditions}
                setSelectedCondition={setSelectedCondition}
                searchTerm={searchTerm}
                setSearchTerm={setSearchTerm}
              />
            </ErrorBoundary>
          )}

          {activeTab === 'quiz' && (
            <ErrorBoundary>
              <QuizTab 
                quizQuestions={quizQuestions}
                setActiveTab={setActiveTab}
                quizProgress={quizProgress}
              />
            </ErrorBoundary>
          )}

          {activeTab === 'progress' && (
            <ErrorBoundary>
              <UserProgress 
                userProgress={quizProgress}
                bookmarkedConditions={bookmarks.bookmarkedConditions}
                onViewCondition={setSelectedCondition}
                onClearProgress={quizProgress.clearHistory}
              />
            </ErrorBoundary>
          )}

          {activeTab === 'pathogens' && (
            <ErrorBoundary>
              <Suspense fallback={<LoadingSpinner message="Loading Pathogen Explorer..." />}>
                <PathogenExplorer 
                  pathogenData={pathogenData}
                  onSelectCondition={setSelectedCondition}
                />
              </Suspense>
            </ErrorBoundary>
          )}

          {activeTab === 'antibiotics' && (
            <ErrorBoundary>
              <Suspense fallback={<LoadingSpinner message="Loading Antibiotic Explorer..." />}>
                <AntibioticExplorer 
                  antibioticData={antibioticData}
                  onSelectCondition={setSelectedCondition}
                />
              </Suspense>
            </ErrorBoundary>
          )}
        </main>

        {/* Condition Detail Modal */}
        <ErrorBoundary>
          <ConditionDetailModal 
            condition={selectedCondition}
            onClose={() => setSelectedCondition(null)}
            isBookmarked={selectedCondition ? bookmarks.isBookmarked(selectedCondition.name) : false}
            onToggleBookmark={() => selectedCondition && bookmarks.toggleBookmark(selectedCondition)}
            allConditions={medicalConditions}
          />
        </ErrorBoundary>
      </div>
    </ErrorBoundary>
  );
};

/**
 * Main App Component
 * Wraps the application with the context provider
 */
function App() {
  return (
    <AppProvider>
      <AppContent />
    </AppProvider>
  );
}

export default App;
